mapper.select:
SELECT FAQ_ID, QESTN_SJ, QESTN_CN, ANSWER_CN, RDCNT AS INQIRE_CO, ATCH_FILE_ID, FRST_REGIST_PNTTM , FRST_REGISTER_ID, LAST_UPDT_PNTTM , LAST_UPDUSR_ID FROM ( SELECT ROWNUM RNUM,FAQ.* FROM ( SELECT FAQ_ID, QESTN_SJ, QESTN_CN, ANSWER_CN, RDCNT, ATCH_FILE_ID, FRST_REGIST_PNTTM , FRST_REGISTER_ID, LAST_UPDT_PNTTM , LAST_UPDUSR_ID FROM LETTNFAQINFO WHERE 1=1
AND QESTN_SJ LIKE '%' || #{searchKeyword} || '%'
ORDER BY FAQ_ID DESC ) FAQ ) WHERE ROWNUM <= #{recordCountPerPage} and RNUM > #{firstIndex}

mapper.update:

			UPDATE	LETTNFAQINFO	SET
				    RDCNT= RDCNT + 1				  			
				  , LAST_UPDT_PNTTM=sysdate
				  , LAST_UPDUSR_ID=#{lastUpdusrId}
 			WHERE  FAQ_ID=#{faqId}


			UPDATE	LETTNFAQINFO	SET
					FAQ_ID=#{faqId} 
				  , QESTN_SJ=#{qestnSj}
				  , QESTN_CN=#{qestnCn}
				  , ANSWER_CN=#{answerCn}
				  , ATCH_FILE_ID=#{atchFileId}				  			
				  , LAST_UPDT_PNTTM=sysdate
				  , LAST_UPDUSR_ID=#{lastUpdusrId}
 			WHERE  FAQ_ID=#{faqId}
