mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT A.QESTNR_ID, A.QUSTNR_SJ qestnrSj, A.QUSTNR_PURPS qestnrPurps, A.QUSTNR_WRITNG_GUIDANCE_CN qestnrWritngGuidanceCn, TO_CHAR(TO_DATE(A.QUSTNR_BGNDE, 'YYYYMMDD'), 'YYYY-MM-DD') qestnrBeginDe, TO_CHAR(TO_DATE(A.QUSTNR_ENDDE, 'YYYYMMDD'), 'YYYY-MM-DD') qestnrEndDe, A.QUSTNR_TRGET qestnrTrget, A.QUSTNR_TMPLAT_ID qestnrTmplatId, A.FRST_REGIST_PNTTM frstRegisterPnttm, A.FRST_REGISTER_ID, B.USER_NM AS FRST_REGISTER_NM, A.LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID FROM LETTNQESTNRINFO A, COMVNUSERMASTER B WHERE 1=1 AND A.FRST_REGISTER_ID = B.ESNTL_ID
AND QUSTNR_SJ LIKE '%' || #{searchKeyword} || '%'
AND B.USER_NM LIKE '%' || #{searchKeyword} || '%'
ORDER BY A.FRST_REGIST_PNTTM DESC
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.delete:

DELETE FROM LETTNQUSTNRRSPNSRESULT
WHERE 1=1
AND QUSTNR_RSPNS_RESULT_ID = #{qestnrQesrspnsId}
