mapper.select:
SELECT uniqId, userTy, userId , userNm, emailAdres, areaNo, middleTelno, endTelno, moblphonNo, groupId, sttus, sbscrbDe FROM( SELECT ESNTL_ID uniqId, 'USR01' userTy, MBER_ID userId, MBER_NM userNm, MBER_EMAIL_ADRES emailAdres, AREA_NO areaNo, MIDDLE_TELNO middleTelno, END_TELNO endTelno, MBTLNUM moblphonNo, GROUP_ID groupId, MBER_STTUS sttus, DATE_FORMAT(SBSCRB_DE,'%Y-%m-%d') sbscrbDe FROM LETTNGNRLMBER ) A WHERE 1=1
AND sttus LIKE #{sbscrbSttus}
AND userId LIKE #{searchKeyword}
AND userNm LIKE '%' #{searchKeyword} '%'
ORDER BY sbscrbDe DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(1) totcnt FROM( SELECT ESNTL_ID uniqId, 'USR01' userTy, MBER_ID userId, MBER_NM userNm, MBER_EMAIL_ADRES emailAdres, AREA_NO areaNo, MIDDLE_TELNO middleTelno, END_TELNO endTelno, MBTLNUM moblphonNo, GROUP_ID groupId, MBER_STTUS sttus, SBSCRB_DE sbscrbDe FROM LETTNGNRLMBER ) A WHERE 1=1
AND sttus LIKE #{sbscrbSttus}
AND userId LIKE #{searchKeyword}
AND userNm LIKE '%' #{searchKeyword} '%'

mapper.update:

